int thongke(thuebao x[], int n, float cd, float ct)
{
	int i, d = 0;
	float sd;
	for (i = 0; i < n; i++)
	{
		sd = x[i].sdtn - x[i].sdtt;
		if ((sd >= cd) && (sd <= ct)) d++;
	}

	return d;
}

