    if kiem_tra_doi_xung(lst):
        return lst
    else:
        for i in range(len(lst)//2):
            if lst[i] != lst[-i-1]:
                for j in range(i+1, len(lst)):
                    if lst[j] == lst[-i-1]:
                        lst[i], lst[j] = lst[j], lst[i]
                        break
        return lst

