#include <iostream>

void Sort(int &a, int &b, int &c){
    if(a>b){
       int tmp = a;
       a = b;
       b = tmp;
    }
    if(a>c){
       int tmp = a;
       a=c;
       c = tmp;
    }
    if(b>c){
       int tmp = b;
       b=c;
       c=tmp;
    }
    return;
}

