#include<iostream>
#include<conio.h>
using namespace std;  
class sophuc
{
              int a,b;
    public:
              void getvalue()
              {
                 cout<<"Hãy nhập phần thực, phần ảo:";
                 cin>>a>>b;
              }
              sophuc operator+(sophuc i)
              {
                            sophuc q;
                            q.a=a+i.a;
                            q.b=b+i.b;
                            return(q);
              }
              sophuc operator-(sophuc i)
              {
                            sophuc q;
                            q.a=a-i.a;
                            q.b=b-i.b;
                            return(q);
              }
              void display()
              {
                            cout<<a<<"+"<<b<<"i"<<"\n";
              }
};

int main()
{

   sophuc i1,i2,kq1,kq2;

   i1.getvalue();
   i2.getvalue();

   kq1 = i1+i2;
   kq2 = i1-i2;

   cout<<"Số phức đã nhập vào:\n";
   i1.display();
   i2.display();

   cout<<"Kết quả cộng, trừ số phức:\n";
   kq1.display();
   kq2.display();

   return 0;
}
